	//google map
	function myMap() {
		var mapCenter = new google.maps.LatLng(51.508742,-0.120850), //latitute, longitude of place
		mapCanvas = document.getElementById("googleMap"),
		mapOptions = {
			center : mapCenter,
			zoom: 5
		},
		mapMarker = new google.maps.Marker({
			position: mapCenter
		}),
		map = new google.maps.Map(mapCanvas, mapOptions);
				//attach marker on map
				mapMarker.setMap(map);
			//zoom to 9 when clicking on marker
			google.maps.event.addListener(mapMarker,'click',function(){
				map.setZoom(9);
				map.setCenter(mapMarker.getPosition());
				//attach info to map
				mapInfo.open(map,mapMarker);
			});
			//centering the location marker in center
			google.maps.event.addDomListener(window,'resize',function(){
				var center = map.getCenter();
				google.maps.event.trigger(map, 'resize');
				map.setCenter(center);
			})
		}


