function initInputNumber(){
	var holder = jQuery('.number-input'),
		handle,
		count= 0;

	holder.each(function(){
		var item = jQuery(this),
			input = item.find('input[type=text]'),
			plus = item.find('.btn-inc'),
			minus = item.find('.btn-dec'),
			max = parseInt(input.data('max-value')),
			min = parseInt(input.data('min-value'));

		plus.on('mousedown', function(e){
			e.preventDefault();
			var val = parseInt(input.val()) + 1;
			if(val <= max){
				input.val(val + ' mil');
				handle = setInterval(function(){
					drawAll(input, true);
				}, 100);
			}
		});
		minus.on('mousedown', function(e){
			e.preventDefault();
			var val = parseInt(input.val()) - 1;
			if(val >= min){
				input.val(val + ' mil');
				handle = setInterval(function(){
					drawAll(input, false);
				}, 100);
			}
		});
		plus.add(minus).on('mouseup', function(){
			clearInterval(handle);
			handle = 0;
			count = 0;
		});

		function drawAll(input, switcher){
			count++;
			if(count > 5){
				if(switcher){
					var val = parseInt(input.val()) + 1;
					if(val <= max){
						input.val(val + 1 + ' mil');
					}
				} else {
					var val = parseInt(input.val()) - 1;
					if(val >= min){
						input.val(val - 1 + ' mil');
					}
				}
			}
		}
	});
}

